  let(:sc_marti) { SchemeOperator.new }
  before do
    sc_marti.email = 'jennifer@back_to_the_future.com'
    sc_marti.first_name = 'Jennifer'
    sc_marti.last_name = 'Smith'
    sc_marti.password = 'mypassword'
