    sc_marti.schemes = [Scheme.first]
    sc_marti.save
    sc_marti.approved = true
    sc_marti.save
    sc_marti.add_role :sc_director

    PermissionsForRole::SchemeOperatorDefinitions.new.permissions_for_role(:sc_director).each do |permission, has|
      sc_marti.add_role permission if has[:checked]
    end
    sc_marti.contacts_d!
    sign_in sc_marti
  end

  after do
    sign_out sc_marti
  end

  let(:valid_attributes) do
    {
      address_type_id: 2,
      business_id:     1,
      first_name:      'nigel',
      last_name:       'surtees',
      title:           'Mr',
      email:           'somebody@emai.com',
      telephone_1:     '09876543212',
      telephone_2:     '09876543233',
      fax:             '987864357389485'
