  let(:valid_session) { {} }

  describe 'GET #index' do
    it 'assigns all contacts as @contacts' do
      contact = Contact.create! valid_attributes
      get :index, business_id: 1, session: valid_session

      contacts = assigns(:contacts)
      retrieved_contact = contacts.where(id: contact.id)
      expect(retrieved_contact).to eq([contact])
    end

    it 'assigns business as @business' do
      get :index, business_id: 1, session: valid_session

      business = assigns(:business)
      expect(business.id).to eq(1)
    end
  end

  describe 'GET #show' do
    it 'assigns the requested contact as @contact' do
      contact = Contact.create! valid_attributes
      get :show, id: contact.to_param, business_id: 1, session: valid_session
